#include "Site.h"
#include "HTMLBlock.h"

Site::Site(QObject *parent) : QObject(parent)
{

}

void Site::setStyleFile(QString fileName)
{
  styleFile = fileName;
}

QString Site::getStyleFile()
{
  return styleFile;
}
#include <QDebug>
void Site::addStyleRule(QString style)
{

  styleRules.insert(style);
  qInfo() << __LINE__ << styleRules;

}

QString Site::getHtmlStyle()
{
//  qInfo() << __LINE__ << styleRules;
  if (styleRules.size() == 0)
    return "";

  return "<link rel='stylesheet' type='text/css' href='" + styleFile + "'>";
}

void Site::setRoot(QString dirName)
{
  root = dirName;
}

QString Site::getRoot()
{
  return root;
}
#include <QDebug>
QString Site::getStyleBlock()
{
  qInfo() << __LINE__ << styleRules;
//  exit(0);

  QString ret;
  for (QString rule: styleRules.values())
    ret += rule;

  return ret;
  //  return ".jumbotron{margin-bottom: 0 !important;}";
}

